    }

    // makeCartCard(card, index){
    //     return(
    //       <Card border="info" className="text-center card" style={{ width: '18rem' }} key={index} >
    //         <Card.Img variant="top" src="holder.js/100px180" src={card.image}/>
    //         <Card.Body>
    //           <Card.Title>{card.breed}</Card.Title>
    //           <Card.Text>
    //             <p>
    //               <h5>${card.price}</h5>
    //               size: {card.size}
    //               <br/>
    //               exercise needed: {card.exercise}
    //             </p>
    //           </Card.Text>
    //           <Button  variant="info" >Remove</Button> 
    //           {/* onClick={this.props.onClick} */}
    //         </Card.Body>
    //       </Card>
    //     )
    //   }

    render(){
        const items = this.props.list.filter(this.matchesFilterSize).filter(this.matchesFilterExercise)
        console.log(items)
        this.sortByPrice(items)
        return(
            <div>
                <NavBar onSelect={this.onSelectFilterSize} onSelect2={this.onSelectFilterExercise} onSelect3={this.sortChoice}/>
                <DisplayList list={items} add={this.addToCart} />   
                <Cart cart={this.state.cart} total={this.state.totalprice} remove={this.removeFromCart}/>
            </div>

        )
    }
}
