import React from 'react'
import {Card, Button, ButtonGroup} from 'react-bootstrap';
import './App.css'

export default class Cart extends React.Component{
    constructor(props){
        super(props)
    }

    // makeCard(card, index){
    //     return(
    //         <div >
    //             <Card border="info" className="text-center card" style={{ width: '18rem' }} key={index} >
    //                 <Card.Img variant="top" src="holder.js/100px180" src={card.image}/>
    //                 <Card.Body>
    //                 <Card.Title>{card.breed}</Card.Title>
    //                 <Card.Text>
    //                     <p>
    //                       <h5>${card.price}</h5>
    //                       size: {card.size}
    //                       <br/>
    //                       exercise needed: {card.exercise}
    //                     </p>
    //                     <ButtonGroup aria-label="Basic example">
    //                         <Button variant="info">-</Button>
    //                         <Button variant="light">{card.quantity}</Button>
    //                         <Button variant="info">+</Button>
    //                     </ButtonGroup>
    //                 </Card.Text>
    //                   <Button  variant="info" onClick={this.props.remove(card)}>Remove</Button> 
    //                 </Card.Body>
    //             </Card>
    //         </div>
    //     )
    // }

    render(){ 
        const total = this.props.total
        // const dogsCart = this.props.cart.map(this.makeCard)
        return(
            <div>
                <h2 /*style={backgroundColor: }*/>Your Pup Cart</h2>
                <div className="cartContainer">
                {/* {dogsCart} */}
                {this.props.cart.map((card, index) => {
                    return(
                        <Card border="info" className="text-center card" style={{ width: '18rem' }} key={index} >
                        <Card.Img variant="top" src="holder.js/100px180" src={card.image}/>
                        <Card.Body>
                        <Card.Title>{card.breed}</Card.Title>
                        <Card.Text>
                              <h5>${card.price}</h5>
                              size: {card.size}
                              <br/>
                              exercise needed: {card.exercise}
                            <ButtonGroup aria-label="Basic example">
                                <Button variant="info">-</Button>
                                <Button variant="light">{card.quantity}</Button>
                                <Button variant="info">+</Button>
                            </ButtonGroup>
                        </Card.Text>
                          <Button  variant="info" onClick={this.props.remove(card)}>Remove</Button> 
                        </Card.Body>
                    </Card>
                    )
                }

                )}
                </div>
                <h4>Total: ${total}</h4> 
                <Button variant="info">Checkout</Button>
                <br/>
            </div>

        )

    }
}
