import React from 'react';
import DisplayList from './DisplayList';
import {Navbar, Nav, DropdownButton, Dropdown} from 'react-bootstrap'
import NavBar from './NavBar'

export default class FilteredList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            size: "All"
        }
    }
    createNav(){
        return(
            <Navbar bg="info" expand="lg" variant="dark">
                <Navbar.Brand href="#home"><h3>Find My Pup</h3></Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link href="#home">My Pup Cart</Nav.Link>
                    </Nav>
                    <DropdownButton id="dropdown-basic-button" title="Filter by: Size" variant="info">
                        <Dropdown.Item href="#/action-4" eventKey="All" onSelect={this.props.onSelect}>All</Dropdown.Item>
                        <Dropdown.Item href="#/action-1">Small</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">Medium</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Large</Dropdown.Item>
                    </DropdownButton>
                    <DropdownButton id="dropdown-basic-button" title="Filter by: Exercise Needs" variant="info">
                        <Dropdown.Item href="#/action-4" eventKey="All" >All</Dropdown.Item>
                        <Dropdown.Item href="#/action-1">Low</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">Medium</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">High</Dropdown.Item>
                    </DropdownButton>
                    <DropdownButton id="dropdown-basic-button" title="Sort by Price" variant="info">
                        <Dropdown.Item href="#/action-1">Lowest to Highest</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">Highest to Lowest</Dropdown.Item>
                    </DropdownButton>
                </Navbar.Collapse>
            </Navbar>
        )
    }
    onSelectFilterSize(event){
        this.setState({size: event})
    }

    matchesFilterSize(item){
        if(this.state.size === "All"){
            return true
        } else if (this.state.size === item.size) {
            return true
        } else {
            return false
        }
    }
    render(){
        return(
            <div>
                <NavBar onSelect={this.onSelectFilterSize}/>
                <DisplayList list={this.props.list.filter(this.matchesFilterSize)} />
            </div>

        )
    }
}
