import React from 'react';
import DisplayList from './DisplayList';
import {Navbar, Nav, DropdownButton, Dropdown} from 'react-bootstrap'
import NavBar from './NavBar'

export default class FilteredList extends React.Component {
    constructor(props){
        super(props)
        this.matchesFilterSize = this.matchesFilterSize.bind(this)
        this.onSelectFilterSize = this.onSelectFilterSize.bind(this)
        this.state = {
            size: "All"

        }

    }

    onSelectFilterSize(event){
        // event.preventDefault()
        this.setState({size: event})
    }

    matchesFilterSize(item){
        if(this.state.size === "All"){
            return true
        } else if (this.state.size === item.size) {
            return true
        } else {
            return false
        }
    }
    render(){
        return(
            <div>
                {/* <NavBar onSelect={this.onSelectFilterSize}/> */}
                <Navbar bg="info" expand="lg" variant="dark">
                    <Navbar.Brand href="#home"><h3>Find My Pup</h3></Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="#home">My Pup Cart</Nav.Link>
                        </Nav>
                        <DropdownButton id="dropdown-basic-button" title="Filter by: Size" variant="info">
                            <Dropdown.Item eventKey="All" onSelect={this.onSelectFilterSize}>All</Dropdown.Item>
                            <Dropdown.Item eventKey="small" onSelect={this.onSelectFilterSize}>Small</Dropdown.Item>
                            <Dropdown.Item>Medium</Dropdown.Item>
                            <Dropdown.Item >Large</Dropdown.Item>
                        </DropdownButton>
                        <DropdownButton id="dropdown-basic-button" title="Filter by: Exercise Needs" variant="info">
                            <Dropdown.Item >All</Dropdown.Item>
                            <Dropdown.Item >Low</Dropdown.Item>
                            <Dropdown.Item >Medium</Dropdown.Item>
                            <Dropdown.Item >High</Dropdown.Item>
                        </DropdownButton>
                        <DropdownButton id="dropdown-basic-button" title="Sort by Price" variant="info">
                            <Dropdown.Item >Low to High</Dropdown.Item>
                            <Dropdown.Item >High to Low</Dropdown.Item>
                        </DropdownButton>
                    </Navbar.Collapse>
                </Navbar>
                <DisplayList list={this.props.list.filter(this.matchesFilterSize)} />

            </div>

        )
    }
}
